#!/usr/bin/env Rscript
#
# Generates the performance distribution plot for the paper in RQ1.
# Input: The `decomposition_scores.csv` file generated by `decompose.sh`.
# Output: The path to the output PDF file.

library(tidyverse)
library(flexplot)

args = commandArgs(trailingOnly=TRUE)

if (length(args)!=2) {
  stop("Please provide an input file and output file.n", call.=FALSE)
}
inputFile = args[1]
outputFile = args[2]


data <- read.csv(inputFile, header = FALSE, col.names = c('Project', 'BugID', 'SmartCommit', 'Flexeme', 'FileUntangling'))
data <- subset(data, select = -c(FileUntangling))
data$BugID <- as_factor(data$BugID)

# Convert to long format
data_long = pivot_longer(data, cols = 3:4, names_to = 'Tool', values_to = 'Performance')

pdf(outputFile)
flexplot(Performance ~ Tool, data = data_long)
dev.off()